fn add(a, b){
    return a + b
}

res = add(1, 2)
print(res)

async fn asyncAdd(a, b){
    return a + b
}

task = asyncAdd(1, 2)
res = coroutine(task)
print(res)

a = 0
if a > 0 {
    print("greater than 0")
} else if a < 0 {
    print("less than 0")
} else {
    print("equal to 0")
}

a = 0
loop {
    print(a)
    a = a + 1
    if a > 10 {
        break
    }
}

a = 0
while a < 10 {
    print(a)
    a = a + 1
}
